var ManClass= $jin_mixin( function( Man, man ){
    man.name= 'Bob'
})

var Boy= ManClass( function( Boy, boy ){
    boy.toString= function( boy ){
        return boy.name
    }
})

_test.ok( Boy().name, 'Bob' )

var ManClass= $jin_mixin( function( Man, man ){
    man.name= 'Bob'
})

var Boy= $jin_class( function( Boy, boy ){
    ManClass.scheme( Boy, boy )

    boy.toString= function( boy ){
        return boy.name
    }
})

_test.ok( Boy().name, 'Bob' )
