_test.ok( Node( document ) )

var Node= $jin_class( function( Node, node ){
    $jin_wrapper.$jin_class_scheme( Node, node )
} )
_test.ok( Node( document ).$, document )

var Node= $jin_class( function( Node, node ){
    $jin_wrapper.$jin_class_scheme( Node, node )
} )

