var Int= $jin_class( function( Int, int ){

    var make= Int.$jin_class_make
    Int.$jin_class_make= function( value ){
        return make( parseInt( value ) )
    }

    $jin_wrapper.$jin_class_scheme( Int, int )
} )

_test.ok( Int( '1.2.3' ).$, 1 )
